"use client";
import { Table, Button, message, Popconfirm, Input, Space, Drawer, Row, Form, Col, Image, Divider, Flex } from 'antd';
import { SearchOutlined } from '@ant-design/icons';
import Highlighter from 'react-highlight-words';
import React, { useEffect, useState, useRef } from 'react'
import { useLoginStore } from '@/store/useLoginStore';
import { useRouter } from 'next/navigation';

const boxStyle = {
  width: '100%',
  // height: 120,
  borderRadius: 6,
  // border: '1px solid #40a9ff',
};

function Home() {
  const loginStatus = useLoginStore(state => state.loginStatus);
  const userId = useLoginStore(state => state.userId);


  const router = useRouter()
  const [data, setData] = useState([]);

  //抽屉-图表
  const [openChart, setOpenChart] = useState(false);
  const [selectedFund, setSelectedFund] = useState('')// 传递点击的基金 
  const [selectedFundId, setSelectedFundId] = useState(0)// 传递点击的基金ID 

  //买入基金
  // Set default values
  const [givenCost, setGivenCost] = useState(10.0); // 默认当前成本
  const [givenNum, setGivenNum] = useState(1.0); // 默认当前持仓
  // Form instance for controlling and accessing form values
  const [form] = Form.useForm();
  const [transactionType, setTransactionType] = useState("")


  const showChart = (record) => {
    setSelectedFund(record.name)
    setSelectedFundId(record.fid.toString().padStart(6, '0'))
    setOpenChart(true);
  };
  const onCloseChart = () => {
    setOpenChart(false);
  };

  //抽屉-买入
  const [openOpera, setOpenOpera] = useState(false);
  const showOpera = async (record, type) => {
    //清空之前的input
    form.setFieldsValue({
      newCost: '',      //清空
      newNum: '',
      cost: '',
      num: '',
    });

    // 开启买入抽屉获取该基金的position
    try {
      const response = await fetch(`/api/getFundPosition?fid=${record.fid}&userId=${userId}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        const data = await response.json();
        console.log(data);
        setGivenCost(data.record.yuanjia);
        setGivenNum(data.record.fenshu);
        form.setFieldsValue({
          givenCost: data.record.yuanjia,
          givenNum: data.record.fenshu,
          // givenCost: givenCost,
          // givenNum: givenNum,
        });
        setSelectedFund(record.name)
        setSelectedFundId(record.fid.toString().padStart(6, '0'))
        setTransactionType(type)  //记录交易类型
        setOpenOpera(true);
      } else if (response.status === 500 && type === "buy") {
        // Handle the case where no records are found in the database
        setGivenCost(0);
        setGivenNum(0);
        form.setFieldsValue({
          givenCost: 0,
          givenNum: 0,
        });
        setSelectedFund(record.name)
        setSelectedFundId(record.fid.toString().padStart(6, '0'))
        setTransactionType(type)  //记录交易类型
        setOpenOpera(true);
        console.log('No records found, keeping default values.');
      } else if (response.status === 500 && type === "sell") {  //没有持仓是，不能打开卖出界面
        console.log('No buy record found, cant sell.');
        messageApi.open({
          type: 'error',
          content: "您没有该基金的持仓记录，无法卖出",
        });
      }
    } catch (error) {
      console.error('Failed to fetch fund details:', error);
    }


  };
  const onCloseOpera = () => {
    setOpenOpera(false);
  };

  //买入基金逻辑：
  //买入表单提交
  const onFinish = async (values) => {
        //直接提交，则(不管是否有预览结果都重新计算)计算newNum和newCost再返回values
        // console.log('Got values:', values);
        // const newNum = Number(givenNum) + Number(values.num); // 最终持仓数
        // const newCost = ((givenCost * givenNum) + (values.cost * values.num)) / newNum; // 最终成本价
        // values.newNum = newNum;
        // values.newCost = newCost;
        // console.log('calculated values:', values);

    //如果不按预览直接提交交易，也必须调用预览按钮的函数，把计算结果覆盖收集回来的form value
    handlePreviewResult();
    values.newNum = form.getFieldValue('newNum');
    values.newCost = form.getFieldValue('newCost');
    //提交修改
    try {
      const response = await fetch(`/api/positionChange?fid=${selectedFundId}&userId=${userId}&newNum=${values.newNum}&newCost=${values.newCost}`, {
        method: 'POST', // 确保使用正确的 HTTP 方法
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const data = await response.json();
      if (response.ok) {
        // console.log(data);
        messageApi.open({
          type: 'success',
          content: data.message,
        });
      } else {
        messageApi.open({
          type: 'error',
          content: data.message,
        });
      }
    } catch (error) {
      console.error('Failed to add to favorites:', error);
      // alert('Failed to add to favorites. Please try again.'); // 显示一个错误消息
    }

  };
  //预览结果
  // Logic for calculating and setting the final holdings and final cost price
  const handlePreviewResult = () => {
    if (transactionType === 'buy') {
      form.validateFields(['cost', 'num']).then((values) => {
        const { cost, num } = values;
        const newNum = Number(givenNum) + Number(num); // 最终持仓数
        const newCost = ((givenCost * givenNum) + (cost * num)) / newNum; // 最终成本价

        // Update form fields with calculated values
        form.setFieldsValue({
          newCost: newCost.toFixed(2),
          newNum: newNum.toFixed(2),
        });
      }).catch((info) => {
        console.log('Validate Failed:', info);
        // message.error('请先输入买入数量和买入价格！');
      });
    }
    if (transactionType === 'sell') {
      form.validateFields(['cost', 'num']).then((values) => {
        const { cost, num } = values;
        const newNum = Number(givenNum) - Number(num); // 最终持仓数
        const newCost = ((givenCost * givenNum) - (cost * num)) / newNum; // 最终成本价

        if(newNum >= 0){      //做基本的检查
          // Update form fields with calculated values
          form.setFieldsValue({
            newCost: newCost.toFixed(2),
            newNum: newNum.toFixed(2),
          });
        }else{
          messageApi.open({
            type: 'error',
            content: "卖出份数超出最大值",
          });
        }
      }).catch((info) => {
        console.log('Validate Failed:', info);
        // message.error('请先输入买入数量和买入价格！');
      });
    }
    console.log(form);
  };

  //搜索Start
  const [searchText, setSearchText] = useState('');
  const [searchedColumn, setSearchedColumn] = useState('');
  const searchInput = useRef(null);
  const handleSearch = (selectedKeys, confirm, dataIndex) => {
    confirm();
    setSearchText(selectedKeys[0]);
    setSearchedColumn(dataIndex);
  };
  const handleReset = (clearFilters) => {
    clearFilters();
    setSearchText('');
  };
  const getColumnSearchProps = (dataIndex) => ({
    filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters, close }) => (
      <div
        style={{
          padding: 8,
        }}
        onKeyDown={(e) => e.stopPropagation()}
      >
        <Input
          ref={searchInput}
          placeholder={`输入 ${dataIndex}`}
          value={selectedKeys[0]}
          onChange={(e) => setSelectedKeys(e.target.value ? [e.target.value] : [])}
          onPressEnter={() => handleSearch(selectedKeys, confirm, dataIndex)}
          style={{
            marginBottom: 8,
            display: 'block',
          }}
        />
        <Space>
          <Button
            type="primary"
            onClick={() => handleSearch(selectedKeys, confirm, dataIndex)}
            icon={<SearchOutlined />}
            size="small"
            style={{
              width: 90,
            }}
          >
            搜索
          </Button>
          <Button
            onClick={() => clearFilters && handleReset(clearFilters)}
            size="small"
            style={{
              width: 90,
            }}
          >
            重置
          </Button>
          <Button
            type="link"
            size="small"
            onClick={() => {
              close();
            }}
          >
            关闭
          </Button>
        </Space>
      </div>
    ),
    filterIcon: (filtered) => (
      <SearchOutlined
        style={{
          color: filtered ? '#1677ff' : undefined,
        }}
      />
    ),
    onFilter: (value, record) =>
      record[dataIndex].toString().toLowerCase().includes(value.toLowerCase()),
    onFilterDropdownOpenChange: (visible) => {
      if (visible) {
        setTimeout(() => searchInput.current?.select(), 100);
      }
    },
    render: (text) =>
      searchedColumn === dataIndex ? (
        <Highlighter
          highlightStyle={{
            backgroundColor: '#ffc069',
            padding: 0,
          }}
          searchWords={[searchText]}
          autoEscape
          textToHighlight={text ? text.toString() : ''}
        />
      ) : (
        text
      ),
  });
  //搜索End

  const columns = [
    {
      title: '基金代码',
      width: '10%',
      dataIndex: 'fid',
      key: 'name',
      fixed: 'left',
      sorter: (a, b) => a.fid - b.fid,
      ...getColumnSearchProps('fid'),
    },
    {
      title: '基金名称',
      width: '25%',
      dataIndex: 'name',
      key: 'age',
      fixed: 'left',
      ...getColumnSearchProps('name'),
    },
    // {
    //   title: '实时涨幅',
    //   dataIndex: 'rate',
    //   key: '1',
    //   width: 50,
    //   defaultSortOrder: 'descend',
    //   sorter: (a, b) => a.rate - b.rate,
    //   render(text, record) {
    //     return {
    //       props: {
    //         style: { color: parseFloat(text) > 0 ? "red" : "green" }
    //       },
    //       children: <div>{text}</div>
    //     };
    //   }
    // },
    {
      title: '实时涨幅',
      dataIndex: 'rate',
      key: '1',
      width: 30,
      defaultSortOrder: 'descend',
      sorter: (a, b) => a.rate - b.rate,
      onCell: (record) => ({
        style: { color: parseFloat(record.rate) > 0 ? "red" : "green" }
      }),
      render: (text) => <div>{text}</div>,
    },

    {
      title: '实时净值',
      dataIndex: 'value',
      key: '2',
      width: 30,
      defaultSortOrder: 'descend',
      sorter: (a, b) => a.value - b.value,
    },
    {
      title: '基金类型',
      dataIndex: 'leixin',
      key: '3',
      width: 40,
    },
    {
      title: '拼音代码',
      dataIndex: 'pinyin',
      key: '4',
      width: 80,
    },
    {
      title: 'Action',
      key: 'operation',
      fixed: 'right',
      width: '18%',
      render: (text, record) =>
        <>
          {contextHolder}
          <Popconfirm
            title="添加自选"
            description="确定要添加到自选吗？"
            onConfirm={() => addToMyFund(record)}
            // onCancel={cancel}
            okText="确认"
            cancelText="取消"
          >
            <Button size='small'>加入自选</Button>
          </Popconfirm>
          <Button onClick={() => showChart(record)} size='small'>查看图表</Button>
          <Popconfirm
            title="交易"
            description="选择交易类型"
            onConfirm={() => showOpera(record, "buy")}
            onCancel={() => showOpera(record, "sell")}
            okText="买入"
            cancelText="卖出"
          >
            {/* <Button onClick={() => showOpera(record)} size='small'>买入</Button> */}
            <Button size='small'>交易</Button>
          </Popconfirm>
        </>
    },
  ];

  //全局提示
  const [messageApi, contextHolder] = message.useMessage();

  const error = () => {
    messageApi.open({
      type: 'error',
      content: 'This is an error message',
    });
  };

  // 加入自选
  const addToMyFund = async (record) => {
    try {
      const response = await fetch(`/api/addToMyFund?fid=${record.fid}&userId=${userId}&fundName=${record.name}`, {
        method: 'POST', // 确保使用正确的 HTTP 方法
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const data = await response.json();
      if (response.ok) {
        // alert(data.message); // 或者使用更友好的用户通知方式
        messageApi.open({
          type: 'success',
          content: data.message,
        });
      } else {
        // 处理错误情况，例如基金已存在于列表中
        // alert(data.message); // 根据 API 返回的消息显示提示
        messageApi.open({
          type: 'eorror',
          content: data.message,
        });
      }
    } catch (error) {
      console.error('Failed to add to favorites:', error);
      alert('Failed to add to favorites. Please try again.'); // 显示一个错误消息
    }
  };



  useEffect(() => {
    if (!loginStatus) {
      router.push('/login');
    }

    async function fetchData() {
      try {
        const response = await fetch('/api/allFund');
        if (response.ok) {
          const jsonData = await response.text(); // 先获取文本内容
          try {
            const obj = JSON.parse(jsonData); // 尝试解析文本为JSON
            // console.log(obj);
            const dataWithKeys = obj.map((item) => ({
              ...item,
              key: item.fid, // 使用更稳定的唯一标识作为key
            }));
            setData(dataWithKeys);
          } catch (error) {
            console.error("Parsing error:", error);
            console.log("Received text:", jsonData);
          }
        } else {
          console.error("API call failed:", response.statusText);
        }
      } catch (error) {
        console.error("Fetching error:", error);
        setData({ error: "Failed to load data." }); // 设置错误信息，以便在页面上显示
      }
    }

    fetchData();
  }, [loginStatus, router]);

  return (
    <>
      <Table
        columns={columns}
        dataSource={data}
        scroll={{
          x: 1000,
          y: 600,
        }}
        locale={{
          triggerDesc: '点击降序排列',
          triggerAsc: '点击升序排列',
          cancelSort: '点击取消排序'
        }}
      />
      <Drawer
        title={selectedFund}
        onClose={onCloseChart}
        open={openChart}
        size='large'
      >
        <Space direction="vertical" size="middle" style={{ display: 'flex' }}>
          <h2>单位净值走势图</h2>
          <Image
            width={700}
            src={`https://j3.dfcfw.com/images/JJJZ1/${selectedFundId}.png`}
            placeholder={
              <Image
                preview={false}
                src="data:image/png;base64,"
                width={700}
                height={245}
              />
            }
            fallback="data:image/png;base64,"
          />
          <h2>持仓前十股票</h2>
          <Image
            width={700}
            src={`https://j6.dfcfw.com/charts/StockPos/${selectedFundId}.png?rt=NaN`}
            placeholder={
              <Image
                preview={false}
                src="data:image/png;base64,"
                width={700}
                height={245}
              />
            }
            fallback="data:image/png;base64,"
          />
        </Space>
      </Drawer>

      <Drawer
        title={(transactionType === "buy" ? "买入" : "卖出") + ":" + selectedFund}
        onClose={onCloseOpera}
        open={openOpera}
      >
        {/* API 获取数据表单 */}
        <Form form={form} layout="vertical" autoComplete="off" onFinish={onFinish}>
          <Col span={30}>
            <Form.Item name="givenCost" label="当前成本" initialValue={givenCost}>
              <Input placeholder="当前成本" disabled={true} />
            </Form.Item>
          </Col>
          <Col span={30}>
            <Form.Item name="givenNum" label="当前持仓" initialValue={givenNum}>
              <Input placeholder="当前持仓" disabled={true} />
            </Form.Item>
          </Col>

          {/* 用户输入表单 */}
          <Col span={30}>
            <Form.Item
              name="cost"
              label="买入/卖出 价格"
              rules={[
                {
                  required: 'ture',
                  message: '请输入正确的价格',
                  pattern: '^([-]?[1-9][0-9]*|0)$'
                },
              ]}
            >
              <Input placeholder="请输入买入/卖出价格：" />
            </Form.Item>
          </Col>
          <Col span={30}>

            <Form.Item
              name="num"
              label="买入/卖出 数量"
              rules={[
                {
                  required: 'true',
                  message: '请输入正确的数量',
                  pattern: '^([-]?[1-9][0-9]*|0)$'
                },
              ]}
            >
              <Input placeholder="请输入买入/卖出数量：" />
            </Form.Item>
            <Row span={50}>
              <Flex style={boxStyle} justify={'space-evenly'} align={'center'}>
                <Button type="primary" htmlType="submit">确认交易</Button>
                <Button onClick={handlePreviewResult}>预览结果</Button>
              </Flex>
            </Row>
          </Col>
          <Divider />

          {/* 前端计算预览结果表单 */}
          <Col span={30}>
            <Form.Item
              name="newCost"
              label="最终成本价"
              disable="ture"
            // initialValue={0}
            >
              <Input placeholder="最终成本价" disabled={true} />
            </Form.Item>
          </Col>
          <Col span={30}>
            <Form.Item
              name="newNum"
              label="最终持仓数"
            // initialValue={0}
            >
              <Input placeholder="最终持仓数" disabled={true} />
            </Form.Item>
          </Col>
        </Form>
      </Drawer>
    </>
  );
}

export default Home;  